    return(

        <StyledTetrisWrapper>
            <StyledTetris>
            <Stage stage={stage}/>
            <aside>
                {gameOver ? (
                    <Display gameOver={gameOver} text="Game Over!" />
                ) : (
                <div>
                <Display text="Score" />
                <Display text="Rows" />
                <Display text="Level" /> 
                </div> 
                )}
                <StartButton />
            </aside>
            </StyledTetris> 
        </StyledTetrisWrapper>
    );
};

export default Tetris;
